// Normalize input and extract development prompt
const input = $input.item.json;
const body = input.body || {};

// Extract the development prompt
const prompt = body.prompt || body.request || body.message || 'No prompt provided';

// Extract mode (quick or full unified prompting)
const unifiedPromptingMode = body.mode || 'full'; // 'quick' or 'full'

// Extract any additional context
const context = body.context || {};

return {
  json: {
    prompt: prompt,
    unifiedPromptingMode: unifiedPromptingMode,
    context: context,
    timestamp: new Date().toISOString(),
    sessionId: body.sessionId || `session-${Date.now()}`
  }
};

