// Parse AI response and extract comprehensive action plan
const aiResponse = $input.item.json.choices?.[0]?.message?.content || '{}';
let actionPlan;

try {
  // Extract JSON from AI response (might be wrapped in markdown)
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  actionPlan = JSON.parse(jsonMatch ? jsonMatch[0] : aiResponse);
} catch (e) {
  // Fallback if parsing fails
  actionPlan = {
    analysis: {
      promptType: 'unknown',
      systemsAffected: ['game', 'curriculum', 'book', 'website'],
      complexity: 'medium'
    },
    actionPlan: {
      layers: [
        { layer: 1, name: 'Foundation', tasks: ['Parse prompt', 'Identify systems'] },
        { layer: 2, name: 'Application', tasks: ['Apply changes'] },
        { layer: 3, name: 'Integration', tasks: ['Integrate systems'] },
        { layer: 4, name: 'Mastery', tasks: ['Verify integration'] }
      ]
    },
    integrationPoints: { game: {}, curriculum: {}, book: {}, website: {} },
    memoryContext: { bookUpdates: [], lessonUpdates: [], websiteUpdates: [] },
    unifiedPromptingQuestions: { mode: 'full', questions: [], answers: [] },
    successCriteria: ['All systems updated', 'Integration verified']
  };
}

// Merge with original input data
return {
  json: {
    ...$('Normalize Input & Extract Prompt').item.json,
    actionPlan: actionPlan,
    parsedAt: new Date().toISOString()
  }
};

// Load unified curriculum schema and prepare for updates
const fs = require('fs');
const path = require('path');

// Get workflow path from environment
const workflowPath = $env.WORKFLOW_PATH || process.env.WORKFLOW_PATH || '/Users/rashadwest/Sportstechwest/workflows/BallCODE-Book';
const schemaPath = path.join(workflowPath, 'CURRICULUM-DATA-EXAMPLE.json');

let curriculumSchema = {};

try {
  // Try to read schema file
  if (typeof fs !== 'undefined' && fs.readFileSync) {
    curriculumSchema = JSON.parse(fs.readFileSync(schemaPath, 'utf8'));
  } else {
    // Fallback: return schema structure
    curriculumSchema = {
      books: [],
      curriculum: {},
      metadata: { version: '1.0.0', lastUpdated: new Date().toISOString() }
    };
  }
} catch (e) {
  // If file doesn't exist or can't read, use empty structure
  curriculumSchema = {
    books: [],
    curriculum: {},
    metadata: { version: '1.0.0', lastUpdated: new Date().toISOString() }
  };
}

// Get action plan from previous node
const actionPlan = $('Parse Action Plan').item.json.actionPlan;

// Prepare integration data
return {
  json: {
    ...$('Parse Action Plan').item.json,
    curriculumSchema: curriculumSchema,
    schemaPath: schemaPath,
    readyForIntegration: true
  }
};

