// Merge all system updates and prepare for integration
const gameUpdates = $('Generate Game Updates (AI)').item?.json?.choices?.[0]?.message?.content || null;
const curriculumUpdates = $('Generate Curriculum Updates (AI)').item?.json?.choices?.[0]?.message?.content || null;
const bookUpdates = $('Generate Book Updates (AI)').item?.json?.choices?.[0]?.message?.content || null;
const websiteUpdates = $('Generate Website Updates (AI)').item?.json?.choices?.[0]?.message?.content || null;

// Parse each update
let parsedGame = null;
let parsedCurriculum = null;
let parsedBook = null;
let parsedWebsite = null;

if (gameUpdates) {
  try {
    const jsonMatch = gameUpdates.match(/\{[\s\S]*\}/);
    parsedGame = JSON.parse(jsonMatch ? jsonMatch[0] : gameUpdates);
  } catch (e) {}
}

if (curriculumUpdates) {
  try {
    const jsonMatch = curriculumUpdates.match(/\{[\s\S]*\}/);
    parsedCurriculum = JSON.parse(jsonMatch ? jsonMatch[0] : curriculumUpdates);
  } catch (e) {}
}

if (bookUpdates) {
  try {
    const jsonMatch = bookUpdates.match(/\{[\s\S]*\}/);
    parsedBook = JSON.parse(jsonMatch ? jsonMatch[0] : bookUpdates);
  } catch (e) {}
}

if (websiteUpdates) {
  try {
    const jsonMatch = websiteUpdates.match(/\{[\s\S]*\}/);
    parsedWebsite = JSON.parse(jsonMatch ? jsonMatch[0] : websiteUpdates);
  } catch (e) {}
}

// Get original action plan
const actionPlan = $('Load Unified Curriculum Schema').item.json.actionPlan;

// Compile memory context
const memoryContext = {
  timestamp: new Date().toISOString(),
  sessionId: $('Normalize Input & Extract Prompt').item.json.sessionId,
  prompt: $('Normalize Input & Extract Prompt').item.json.prompt,
  bookUpdates: parsedBook?.memoryContext?.bookUpdates || [],
  lessonUpdates: parsedBook?.memoryContext?.lessonUpdates || [],
  websiteUpdates: parsedWebsite?.memoryContext?.websiteUpdates || [],
  gameUpdates: parsedGame?.integrationData || {},
  curriculumUpdates: parsedCurriculum ? 'Schema updated' : null
};

return {
  json: {
    ...$('Load Unified Curriculum Schema').item.json,
    gameUpdates: parsedGame,
    curriculumUpdates: parsedCurriculum,
    bookUpdates: parsedBook,
    websiteUpdates: parsedWebsite,
    memoryContext: memoryContext,
    allSystemsUpdated: true,
    readyForFileWriting: true
  }
};

// Save memory context to file for future reference
// This ensures we remember what was updated for books, lessons, and website

const memoryContext = $json.memoryContext;
const workflowPath = $env.WORKFLOW_PATH || process.env.WORKFLOW_PATH || '/Users/rashadwest/Sportstechwest/workflows/BallCODE-Book';

// Prepare memory file content
const memoryFile = {
  sessionId: memoryContext.sessionId,
  timestamp: memoryContext.timestamp,
  prompt: memoryContext.prompt,
  updates: {
    books: memoryContext.bookUpdates,
    lessons: memoryContext.lessonUpdates,
    website: memoryContext.websiteUpdates,
    game: memoryContext.gameUpdates,
    curriculum: memoryContext.curriculumUpdates
  },
  integrationStatus: 'All systems updated and integrated'
};

// Note: In n8n Code node, we can't write files directly
// This will be handled by executeCommand node or external script
return {
  json: {
    ...$json,
    memoryFileContent: JSON.stringify(memoryFile, null, 2),
    memoryFilePath: `${workflowPath}/memory-context-${memoryContext.sessionId}.json`,
    readyToSaveMemory: true
  }
};

// Finalize workflow: compile completion report with all integration points
const actionPlan = $('Merge All System Updates').item.json.actionPlan;
const memoryContext = $('Prepare Memory Context for Saving').item.json.memoryContext;

const report = {
  status: 'success',
  timestamp: new Date().toISOString(),
  sessionId: memoryContext.sessionId,
  prompt: memoryContext.prompt,
  systemsUpdated: {
    game: !!$('Merge All System Updates').item.json.gameUpdates,
    curriculum: !!$('Merge All System Updates').item.json.curriculumUpdates,
    book: !!$('Merge All System Updates').item.json.bookUpdates,
    website: !!$('Merge All System Updates').item.json.websiteUpdates
  },
  integrationStatus: 'All systems integrated successfully',
  memoryContextSaved: true,
  memoryFilePath: $('Prepare Memory Context for Saving').item.json.memoryFilePath,
  actionPlanLayers: actionPlan.actionPlan.layers,
  successCriteria: actionPlan.successCriteria,
  nextSteps: [
    'Review generated files',
    'Test integration points',
    'Verify curriculum schema updates',
    'Deploy website updates',
    'Test game exercises'
  ]
};

return { json: report };
