// Simple Discord Investment Intelligence Analysis
const messages = $input.all();
const processedMessages = [];

// Investment keywords
const investmentKeywords = ['funding', 'round', 'series', 'seed', 'ipo', 'startup', 'unicorn', 'valuation', 'acquisition', 'merger'];
const sentimentPositive = ['bullish', 'moon', 'rocket', 'buy', 'pump', 'up', 'good', 'great'];
const sentimentNegative = ['bearish', 'dump', 'crash', 'sell', 'down', 'bad', 'terrible'];
const urgentWords = ['breaking', 'urgent', 'alert', 'news', 'confirmed'];

for (const message of messages) {
  try {
    const messageData = message.json;
    const content = messageData.content || '';
    const author = messageData.author || {};
    const channel = messageData.channel || {};

    if (!content.trim()) continue;

    const lowerContent = content.toLowerCase();

    // Calculate relevance score
    let relevanceScore = 0;
    investmentKeywords.forEach(keyword => {
      if (lowerContent.includes(keyword)) relevanceScore += 10;
    });

    // Calculate sentiment
    let sentimentScore = 0;
    sentimentPositive.forEach(word => {
      if (lowerContent.includes(word)) sentimentScore += 1;
    });
    sentimentNegative.forEach(word => {
      if (lowerContent.includes(word)) sentimentScore -= 1;
    });

    let sentiment = 'neutral';
    if (sentimentScore > 0) sentiment = 'bullish';
    else if (sentimentScore < 0) sentiment = 'bearish';

    // Check urgency
    const isUrgent = urgentWords.some(word => lowerContent.includes(word));

    // Calculate overall score
    let overallScore = relevanceScore;
    if (isUrgent) overallScore += 20;
    if (Math.abs(sentimentScore) >= 2) overallScore += 15;

    // Determine priority
    let priority = 'low';
    if (overallScore >= 40) priority = 'critical';
    else if (overallScore >= 25) priority = 'high';
    else if (overallScore >= 15) priority = 'medium';

    const processedMessage = {
      timestamp: new Date().toISOString(),
      author: author.username || 'unknown',
      channel: channel.name || 'unknown',
      content: content.substring(0, 300),
      relevanceScore: relevanceScore,
      sentiment: sentiment,
      sentimentScore: sentimentScore,
      isUrgent: isUrgent,
      overallScore: overallScore,
      priority: priority,
      actionable: overallScore >= 25
    };

    processedMessages.push(processedMessage);

  } catch (error) {
    console.log('Error processing message:', error.message);
  }
}

return processedMessages;
