// Simple Spam Filter - Block obvious spam only
const items = $input.all();
const processedEmails = [];

// Keywords that indicate spam
const spamKeywords = [
  'buy now', 
  'free money', 
  'click here', 
  'limited time', 
  'act now', 
  'winner', 
  'congratulations',
  'prize',
  'claim',
  'click below'
];

for (const item of items) {
  const email = item.json;
  const subject = (email.subject || '').toLowerCase();
  const body = (email.text || email.textPlain || '').toLowerCase();
  const from = (email.from || '').toLowerCase();
  const fullText = `${subject} ${body}`;

  // Check if it's spam
  let isSpam = false;

  // Count spam keywords
  const spamCount = spamKeywords.filter(k => fullText.includes(k)).length;

  // If 2+ spam keywords OR from noreply address = spam
  if (spamCount >= 2 || from.includes('noreply') || from.includes('no-reply')) {
    isSpam = true;
  }

  // Only process non-spam emails
  if (!isSpam) {
    processedEmails.push({
      json: {
        ...email,
        spam_filtered: true
      }
    });
  }
  // Spam emails are just not included in output (blocked)
}

return processedEmails;

// SIMPLE SPAM FILTER - Run Once for All Items mode
const items = $input.all();
const filteredItems = [];

// Keywords that mean "don't reply"
const spamKeywords = ['buy now', 'free money', 'click here', 'winner', 'prize'];
const autoReplyKeywords = ['out of office', 'auto reply', 'automatic response'];
const systemKeywords = ['password reset', 'verify your email', 'confirmation code'];
const courseKeywords = ['new course', 'continue learning', 'lesson available'];
const noReplyPatterns = ['noreply@', 'no-reply@', 'donotreply@', 'notifications@'];
const ignoredDomains = ['datacamp.com', 'linkedin.com', 'twitter.com', 'facebook.com'];

// Process each email
for (let i = 0; i < items.length; i++) {
  const email = items[i].json;
  const subject = (email.subject || '').toLowerCase();
  const body = (email.text || email.textPlain || '').toLowerCase();
  const from = (email.from || '').toLowerCase();
  const fullText = subject + ' ' + body;

  let shouldBlock = false;

  // Check 1: No-reply addresses
  for (let pattern of noReplyPatterns) {
    if (from.includes(pattern)) {
      shouldBlock = true;
      break;
    }
  }

  // Check 2: Ignored domains (Datacamp, LinkedIn, etc.)
  for (let domain of ignoredDomains) {
    if (from.includes(domain)) {
      shouldBlock = true;
      break;
    }
  }

  // Check 3: Spam keywords (2 or more = spam)
  let spamCount = 0;
  for (let keyword of spamKeywords) {
    if (fullText.includes(keyword)) {
      spamCount++;
    }
  }
  if (spamCount >= 2) {
    shouldBlock = true;
  }

  // Check 4: Auto-replies
  for (let keyword of autoReplyKeywords) {
    if (fullText.includes(keyword)) {
      shouldBlock = true;
      break;
    }
  }

  // Check 5: System notifications
  for (let keyword of systemKeywords) {
    if (fullText.includes(keyword)) {
      shouldBlock = true;
      break;
    }
  }

  // Check 6: Course notifications (Datacamp, etc.)
  for (let keyword of courseKeywords) {
    if (fullText.includes(keyword)) {
      shouldBlock = true;
      break;
    }
  }

  // Only add to output if NOT blocked
  if (!shouldBlock) {
    filteredItems.push({
      json: email
    });
  } else {
    // Log blocked emails so you can see what's being filtered
    console.log('🚫 BLOCKED: ' + from + ' - ' + subject);
  }
}

// Return array of items (required for "Run Once for All Items" mode)
return filteredItems;
