// Normalize input from any trigger type
const input = $input.item.json;
const body = input.body || {};

// Detect trigger type from input structure
let triggerType = 'scheduled';
if (body.ref) {
  triggerType = 'github';
} else if (body.request) {
  triggerType = 'webhook';
} else if (input.timestamp) {
  triggerType = 'scheduled';
}

// Extract request message
const request = body.request || body.head_commit?.message || 'Automated build from scheduled trigger';

// Extract branch
const branch = body.ref ? body.ref.replace('refs/heads/', '') : 'main';

return {
  json: {
    request: request,
    triggerType: triggerType,
    timestamp: new Date().toISOString(),
    branch: branch,
    commitMessage: body.head_commit?.message || 'Scheduled build'
  }
};

// Parse AI response and extract action plan
const aiResponse = $input.item.json.choices?.[0]?.message?.content || '{}';
let actionPlan;

try {
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  actionPlan = JSON.parse(jsonMatch ? jsonMatch[0] : aiResponse);
} catch (e) {
  actionPlan = {
    needsUnityEdits: false,
    needsBuild: true,
    needsDeploy: true,
    estimatedTime: '15 minutes',
    priority: 'medium'
  };
}

const shouldProceed = actionPlan.needsBuild || actionPlan.needsUnityEdits || actionPlan.needsDeploy;

// Early exit if no action needed
if (!shouldProceed) {
  return {
    json: {
      ...$('Normalize Input').item.json,
      actionPlan: actionPlan,
      shouldProceed: false,
      status: 'skipped',
      reason: 'No action needed'
    }
  };
}

return {
  json: {
    ...$('Normalize Input').item.json,
    actionPlan: actionPlan,
    shouldProceed: true,
    needsUnityEdits: actionPlan.needsUnityEdits,
    needsBuild: actionPlan.needsBuild,
    needsDeploy: actionPlan.needsDeploy
  }
};

// Get environment variables - try multiple methods
let repoUrl = $env.UNITY_REPO_URL || process.env.UNITY_REPO_URL || '';
let projectPath = $env.UNITY_PROJECT_PATH || process.env.UNITY_PROJECT_PATH || '';

// Debug: Log what we're getting
const debug = {
  hasEnvRepoUrl: !!$env.UNITY_REPO_URL,
  hasEnvProjectPath: !!$env.UNITY_PROJECT_PATH,
  hasProcessRepoUrl: !!process.env.UNITY_REPO_URL,
  hasProcessProjectPath: !!process.env.UNITY_PROJECT_PATH,
  repoUrlValue: repoUrl,
  projectPathValue: projectPath
};

// Check if we have previous data to merge
const previousData = $('Parse AI Response')?.item?.json || {};

// Handle missing environment variables gracefully
// Instead of throwing error, return data with error flag
// This allows workflow to continue and show helpful message
if (!repoUrl || repoUrl.trim() === '') {
  return {
    json: {
      ...previousData,
      repoUrl: '',
      projectPath: projectPath.trim() || '',
      repoUrlSet: false,
      projectPathSet: !!projectPath,
      debug: debug,
      error: 'UNITY_REPO_URL environment variable is not set. Please set it in n8n Settings → Environment Variables.',
      errorDetails: {
        message: 'Missing UNITY_REPO_URL',
        currentValue: repoUrl,
        instructions: 'Go to n8n Settings → Environment Variables and add UNITY_REPO_URL with your GitHub repository URL'
      }
    }
  };
}

if (!projectPath || projectPath.trim() === '') {
  return {
    json: {
      ...previousData,
      repoUrl: repoUrl.trim(),
      projectPath: '',
      repoUrlSet: true,
      projectPathSet: false,
      debug: debug,
      error: 'UNITY_PROJECT_PATH environment variable is not set. Please set it in n8n Settings → Environment Variables.',
      errorDetails: {
        message: 'Missing UNITY_PROJECT_PATH',
        currentValue: projectPath,
        instructions: 'Go to n8n Settings → Environment Variables and add UNITY_PROJECT_PATH with the path where the project should be cloned'
      }
    }
  };
}

// Return variables for next node (merge with previous data)
return {
  json: {
    ...previousData,
    repoUrl: repoUrl.trim(),
    projectPath: projectPath.trim(),
    repoUrlSet: true,
    projectPathSet: true,
    debug: debug,
    error: null
  }
};

// Finalize workflow: compile report, send notification, prepare webhook response
const request = $('Normalize Input').item.json.request;
const actionPlan = $('Get Git Variables').item.json.actionPlan;
const triggerType = $('Normalize Input').item.json.triggerType;

// Compile completion report
const report = {
  status: 'success',
  timestamp: new Date().toISOString(),
  request: request,
  triggerType: triggerType,
  actionPlan: actionPlan,
  buildCompleted: true,
  deployCompleted: true,
  siteUrl: 'https://' + ($env.NETLIFY_SITE_NAME || 'ballcode-game') + '.netlify.app',
  message: 'Unity workflow completed successfully. Site: https://' + ($env.NETLIFY_SITE_NAME || 'ballcode-game') + '.netlify.app',
  hasNotificationURL: !!$env.WEBHOOK_NOTIFICATION_URL,
  isWebhookTrigger: triggerType === 'webhook' || triggerType === 'github'
};

return { json: report };
