// Normalize Input - PRODUCTION READY
// CRITICAL FIX: Preserve ALL data + Check for provided actionPlan
const inputData = $input.item.json || {};
const body = inputData.body || {};
const directData = inputData.request ? inputData : {};

// Extract request - ALL possible sources
let request = directData.request || body.request || inputData.request || body.head_commit?.message || 'Automated build from scheduled trigger';

// Extract actionPlan - PRIORITY: Use provided plan if exists
let actionPlan = directData.actionPlan || body.actionPlan || inputData.actionPlan;

// Check if actionPlan is valid and provided
let hasProvidedActionPlan = !!(actionPlan && typeof actionPlan === 'object' && Object.keys(actionPlan).length > 0);

// Determine trigger type
let triggerType = directData.triggerType || body.triggerType || inputData.triggerType;
if (!triggerType) {
  if (body.ref || inputData.ref) {
    triggerType = 'github';
  } else if (request !== 'Automated build from scheduled trigger') {
    triggerType = 'webhook';
  } else {
    triggerType = 'scheduled';
  }
}

// CRITICAL FIX: For scheduled triggers, always build (hourly builds goal)
// For webhook/GitHub triggers, use AI analysis (smart)
if (triggerType === 'scheduled') {
  // Scheduled trigger = always build (hourly builds running automatically)
  if (!actionPlan) {
    actionPlan = {};
  }
  actionPlan.needsBuild = true;
  actionPlan.needsDeploy = true;
  actionPlan.needsUnityEdits = false;
  hasProvidedActionPlan = true; // Skip AI analysis for scheduled
}

// If actionPlan provided, ensure needsUnityEdits is set correctly
if (hasProvidedActionPlan && actionPlan.unityEdits && actionPlan.unityEdits.length > 0) {
  actionPlan.needsUnityEdits = true;
}

// Calculate shouldProceed based on actionPlan
let shouldProceed = false;
if (hasProvidedActionPlan) {
  shouldProceed = actionPlan.needsBuild || actionPlan.needsUnityEdits || actionPlan.needsDeploy || false;
} else {
  // Will be calculated after AI analysis (for webhook/GitHub triggers)
  shouldProceed = null;
}

return {
  json: {
    ...inputData,
    ...body,
    ...directData,
    request: request,
    triggerType: triggerType,
    actionPlan: actionPlan,
    hasProvidedActionPlan: hasProvidedActionPlan,
    shouldProceed: shouldProceed,
    timestamp: new Date().toISOString(),
    branch: (body.ref || inputData.ref) ? (body.ref || inputData.ref).replace('refs/heads/', '') : (body.branch || directData.branch || 'main'),
    commitMessage: body.head_commit?.message || body.commitMessage || directData.commitMessage || 'Scheduled build'
  }
};

// Parse AI response and extract action plan (PRODUCTION READY)
// CRITICAL: preserve Normalize Input context so request/triggerType/paths don't get lost.
const base = $('Normalize Input').item.json || {};
const aiResponse = $input.item.json.choices?.[0]?.message?.content || '{}';
let actionPlan;

try {
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  actionPlan = JSON.parse(jsonMatch ? jsonMatch[0] : aiResponse);
} catch (e) {
  actionPlan = {
    needsUnityEdits: false,
    needsBuild: true,
    needsDeploy: true,
    estimatedTime: '15 minutes',
    priority: 'medium'
  };
}

// Hybrid behavior:
// - scheduled: always build+deploy (verification cadence)
// - webhook/github: respect AI (smart skip)
const triggerType = String(base.triggerType || '').toLowerCase();
const isScheduled = triggerType === 'scheduled';

if (isScheduled) {
  actionPlan.needsBuild = true;
  actionPlan.needsDeploy = true;
}

const shouldProceed = Boolean(actionPlan.needsBuild || actionPlan.needsUnityEdits || actionPlan.needsDeploy);

return {
  json: {
    ...base,
    actionPlan: actionPlan,
    shouldProceed: shouldProceed
  }
};

// Merge Action Plans - Use provided if exists, otherwise use AI-generated
const inputData = $input.item.json || {};
const providedPlan = inputData.actionPlan;
const hasProvided = inputData.hasProvidedActionPlan;

// If we have provided plan, use it; otherwise use AI-generated
let finalActionPlan = providedPlan;
if (!hasProvided && inputData.actionPlan) {
  finalActionPlan = inputData.actionPlan;
}

// Ensure actionPlan boolean values are correct - CRITICAL FIX: Handle strings and booleans
if (finalActionPlan) {
  // Convert needsUnityEdits
  if (typeof finalActionPlan.needsUnityEdits !== 'boolean') {
    if (finalActionPlan.unityEdits && Array.isArray(finalActionPlan.unityEdits) && finalActionPlan.unityEdits.length > 0) {
      finalActionPlan.needsUnityEdits = true;
    } else {
      finalActionPlan.needsUnityEdits = finalActionPlan.needsUnityEdits === true || finalActionPlan.needsUnityEdits === 'true';
    }
  }
  // Convert needsBuild - handle string "true"/"false" or boolean
  if (typeof finalActionPlan.needsBuild !== 'boolean') {
    finalActionPlan.needsBuild = finalActionPlan.needsBuild === true || finalActionPlan.needsBuild === 'true';
  }
  // Convert needsDeploy - handle string "true"/"false" or boolean
  if (typeof finalActionPlan.needsDeploy !== 'boolean') {
    finalActionPlan.needsDeploy = finalActionPlan.needsDeploy === true || finalActionPlan.needsDeploy === 'true';
  }
}

// Ensure shouldProceed is set - CRITICAL FIX: Recalculate AFTER fixing booleans
let shouldProceed = inputData.shouldProceed;
if (shouldProceed === null || shouldProceed === undefined) {
  // Calculate from actionPlan - ANY true value means proceed
  shouldProceed = !!(finalActionPlan?.needsBuild || finalActionPlan?.needsUnityEdits || finalActionPlan?.needsDeploy);
} else if (shouldProceed === false && finalActionPlan) {
  // Recalculate if false but we have an actionPlan - might be wrong
  shouldProceed = !!(finalActionPlan.needsBuild || finalActionPlan.needsUnityEdits || finalActionPlan.needsDeploy);
}

return {
  json: {
    ...inputData,
    actionPlan: finalActionPlan,
    shouldProceed: shouldProceed
  }
};

// Get Git Variables - Preserves ALL data
const previousData = $input.item.json || {};
const repoUrl = $env.UNITY_REPO_URL || process.env.UNITY_REPO_URL || '';
const projectPath = $env.UNITY_PROJECT_PATH || process.env.UNITY_PROJECT_PATH || '';
const workflowPath = $env.WORKFLOW_PATH || process.env.WORKFLOW_PATH || '';

return {
  json: {
    ...previousData,
    repoUrl: repoUrl || '',
    projectPath: projectPath || '',
    workflowPath: workflowPath || '',
    repoUrlSet: !!repoUrl && repoUrl.trim() !== '',
    projectPathSet: !!projectPath && projectPath.trim() !== '',
    workflowPathSet: !!workflowPath && workflowPath.trim() !== '',
    error: (!repoUrl || !projectPath) ? 'Missing environment variables: UNITY_REPO_URL or UNITY_PROJECT_PATH' : null
  }
};

// Update/Clone Repo - Preserves ALL data
const data = $input.item.json || {};
const repoUrl = data.repoUrl || '';
const projectPath = data.projectPath || '';

if (!repoUrl || !projectPath) {
  return {
    json: {
      ...data,
      success: false,
      error: 'Missing repoUrl or projectPath',
      action: 'skipped'
    }
  };
}

try {
  if (typeof $exec === 'undefined') {
    return {
      json: {
        ...data,
        success: false,
        error: '$exec() not available',
        action: 'error'
      }
    };
  }

  const checkCmd = `test -d "${projectPath}" && echo "exists" || echo "not_exists"`;
  const dirExists = $exec(checkCmd).stdout.trim() === 'exists';

  if (dirExists) {
    const pullResult = $exec(`cd "${projectPath}" && git pull`);
    return {
      json: {
        ...data,
        success: pullResult.exitCode === 0,
        action: 'pulled',
        output: pullResult.stdout || '',
        error: pullResult.stderr || ''
      }
    };
  } else {
    const parentDir = '/' + projectPath.split('/').slice(0, -1).join('/');
    if (parentDir && parentDir !== '/') {
      $exec(`mkdir -p "${parentDir}"`);
    }
    const cloneResult = $exec(`git clone "${repoUrl}" "${projectPath}"`);
    return {
      json: {
        ...data,
        success: cloneResult.exitCode === 0,
        action: 'cloned',
        output: cloneResult.stdout || '',
        error: cloneResult.stderr || ''
      }
    };
  }
} catch (error) {
  return {
    json: {
      ...data,
      success: false,
      error: error.message || 'Unknown error',
      action: 'error'
    }
  };
}

// Compile Completion Report - PRODUCTION READY
const inputData = $input.item.json || {};
let actionPlan = inputData.actionPlan;

if (!actionPlan || typeof actionPlan !== 'object' || Object.keys(actionPlan).length === 0) {
  actionPlan = {
    needsUnityEdits: false,
    needsBuild: true,
    needsDeploy: true,
    estimatedTime: '15 minutes',
    priority: 'medium'
  };
}

let request = inputData.request || 'Unknown request';
let triggerType = inputData.triggerType || 'unknown';
const shouldProceed = inputData.shouldProceed !== false;
const wasSkipped = !shouldProceed;
const netlifySiteName = $env.NETLIFY_SITE_NAME || process.env.NETLIFY_SITE_NAME || 'ballcode-game';

return {
  json: {
    ...inputData,
    status: wasSkipped ? 'skipped' : 'success',
    timestamp: new Date().toISOString(),
    request: request,
    triggerType: triggerType,
    actionPlan: actionPlan,
    buildCompleted: !wasSkipped,
    deployCompleted: !wasSkipped,
    wasSkipped: wasSkipped,
    siteUrl: `https://${netlifySiteName}.netlify.app`,
    message: wasSkipped 
      ? 'Workflow skipped: No build, edits, or deploy needed based on action plan.'
      : `Unity workflow completed successfully. Site: https://${netlifySiteName}.netlify.app`
  }
};
