// L1 (Foundation): normalize trigger input
const input = $input.item.json;
const body = input.body || {};

const isWebhook = !!(body && (body.request || body.message || body.prompt || body.branch));
const triggerType = isWebhook ? 'webhook' : 'scheduled';

const request = String(body.request || body.message || body.prompt || 'Scheduled build');
const branch = String(body.branch || (body.ref ? String(body.ref).replace('refs/heads/', '') : 'main'));

return {
  json: {
    request,
    branch,
    triggerType,
    isWebhook,
    timestamp: new Date().toISOString(),
    proceed: true
  }
};

// L1 (Foundation): env/credential preflight + DEV guardrails
// Guardrail goal: prevent the Mac instance from accidentally running scheduled builds.
//
// Set on Mac:
// - N8N_INSTANCE_ROLE=dev
//
// Set on Pi/prod:
// - N8N_INSTANCE_ROLE=prod
//
// Override (if you really want scheduled builds on dev):
// - ALLOW_SCHEDULED_BUILDS=1

const requiredEnv = [
  'GITHUB_REPO_OWNER',
  'GITHUB_REPO_NAME',
  'GITHUB_WORKFLOW_FILE',
  'NETLIFY_SITE_ID'
];

const missing = requiredEnv.filter((k) => !($env[k] || '').toString().trim());
if (missing.length) {
  return {
    json: {
      ...$json,
      proceed: false,
      status: 'fail',
      error: `Missing required env var(s): ${missing.join(', ')}`,
    }
  };
}

const role = ($env.N8N_INSTANCE_ROLE || 'dev').toString().trim().toLowerCase();
const allowScheduled = ($env.ALLOW_SCHEDULED_BUILDS || '').toString().trim() === '1';

// Block scheduled trigger on dev by default.
if ($json.triggerType === 'scheduled' && role !== 'prod' && !allowScheduled) {
  return {
    json: {
      ...$json,
      proceed: false,
      status: 'skipped',
      skipReason: `Scheduled builds are blocked on ${role}. Set N8N_INSTANCE_ROLE=prod (recommended only on Pi) or set ALLOW_SCHEDULED_BUILDS=1 to override.`,
      instanceRole: role,
      allowScheduled
    }
  };
}

return {
  json: {
    ...$json,
    proceed: true,
    instanceRole: role,
    allowScheduled
  }
};

// L1 (Foundation): overlap prevention via workflow static data lock (TTL)
const MAX_LOCK_SECONDS = 55 * 60; // fits hourly schedule
const nowMs = Date.now();

let staticData;
try {
  staticData = $getWorkflowStaticData('global');
} catch (e) {
  // If static data isn't available (manual test), proceed without lock
  return { json: { ...$json, lock: { enabled: false, reason: 'staticData unavailable' }, proceed: true } };
}

const lockUntilMs = Number(staticData.lockUntilMs || 0);
const lockOwner = String(staticData.lockOwner || '');

if (lockUntilMs && nowMs < lockUntilMs) {
  return {
    json: {
      ...$json,
      proceed: false,
      status: 'skipped',
      skipReason: `Locked until ${new Date(lockUntilMs).toISOString()} (owner=${lockOwner})`,
      lock: { enabled: true, acquired: false, lockUntilMs, lockOwner }
    }
  };
}

const owner = `${$json.triggerType}:${$json.branch}:${$json.timestamp}`;
staticData.lockUntilMs = nowMs + MAX_LOCK_SECONDS * 1000;
staticData.lockOwner = owner;
staticData.lastAttemptAt = new Date(nowMs).toISOString();

return {
  json: {
    ...$json,
    proceed: true,
    lock: { enabled: true, acquired: true, lockUntilMs: staticData.lockUntilMs, lockOwner: owner }
  }
};

// L4 (Mastery): compile report + release lock (best-effort)
let staticData;
try {
  staticData = $getWorkflowStaticData('global');
} catch (e) {
  staticData = null;
}

let gh = { ok: null, status: 'unknown', conclusion: 'unknown', url: '' };
try {
  const runs = $items('Check Latest GitHub Run (AIMCODE L3)')[0].json.workflow_runs || [];
  const latest = runs[0] || {};
  gh.status = latest.status || 'unknown';
  gh.conclusion = latest.conclusion || 'unknown';
  gh.url = latest.html_url || '';
  gh.ok = (gh.status === 'completed' && gh.conclusion === 'success');
} catch (e) {
  gh = { ok: null, status: 'unavailable', conclusion: 'unavailable', url: '' };
}

let nf = { ok: null, state: 'unknown', deployUrl: '' };
try {
  const deploys = $items('Check Latest Netlify Deploy (AIMCODE L3)')[0].json;
  const latest = Array.isArray(deploys) ? (deploys[0] || {}) : {};
  nf.state = latest.state || 'unknown';
  nf.deployUrl = latest.deploy_url || '';
  nf.ok = (nf.state === 'ready' || nf.state === 'published');
} catch (e) {
  nf = { ok: null, state: 'unavailable', deployUrl: '' };
}

if (staticData && $json.lock?.enabled && $json.lock?.acquired) {
  staticData.lockUntilMs = 0;
  staticData.lockOwner = '';
  staticData.lastDispatchAt = $json.timestamp;
}

const siteUrl = 'https://' + ($env.NETLIFY_SITE_NAME || 'ballcode-game') + '.netlify.app';
const status = $json.status || ($json.proceed ? 'ok' : 'skipped');
const message = $json.error
  ? `Preflight failed: ${$json.error}`
  : ($json.skipReason ? `Skipped: ${$json.skipReason}` : `Build dispatched. GH=${gh.status}/${gh.conclusion} NF=${nf.state}`);

return {
  json: {
    status,
    request: $json.request,
    triggerType: $json.triggerType,
    isWebhook: $json.isWebhook,
    branch: $json.branch,
    timestamp: $json.timestamp,
    instanceRole: $json.instanceRole,
    github: gh,
    netlify: nf,
    siteUrl,
    message
  }
};
