// Garvis: Parse website update request
const input = $input.item.json;
const body = input.body || {};

const update = {
  type: body.type || 'content', // content, curriculum, book, deploy
  jobId: body.job_id || body.jobId || `website-${Date.now()}`,
  changes: body.changes || [],
  source: body.source || 'webhook',
  timestamp: new Date().toISOString()
};

// Determine update actions
const actions = [];
if (update.type === 'content' || update.changes.some(c => c.includes('content'))) {
  actions.push('update_content');
}
if (update.type === 'curriculum' || update.changes.some(c => c.includes('curriculum'))) {
  actions.push('update_curriculum_pages');
}
if (update.type === 'book' || update.changes.some(c => c.includes('book'))) {
  actions.push('update_book_pages');
}
if (update.type === 'deploy' || update.changes.some(c => c.includes('deploy'))) {
  actions.push('deploy_netlify');
}

return {
  json: {
    ...update,
    actions,
    status: 'parsed'
  }
};

// Garvis: Update website content
const update = $json;

// In full implementation, this would:
// 1. Read current website files
// 2. Apply content updates
// 3. Generate new pages if needed
// 4. Update navigation
// 5. Optimize images/assets

const result = {
  action: 'update_content',
  filesUpdated: [],
  pagesGenerated: [],
  navigationUpdated: true,
  assetsOptimized: true,
  status: 'success'
};

return {
  json: {
    ...update,
    result
  }
};

// Garvis: Generate curriculum pages from schema
const update = $json;

// In full implementation, this would:
// 1. Read curriculum schema
// 2. Generate curriculum pages
// 3. Update curriculum navigation
// 4. Link to books/games

const result = {
  action: 'update_curriculum_pages',
  pagesGenerated: ['curriculum/index.html', 'curriculum/episode1.html'],
  navigationUpdated: true,
  status: 'success'
};

return {
  json: {
    ...update,
    result
  }
};

// Garvis: Update book pages
const update = $json;

// In full implementation, this would:
// 1. Read book content
// 2. Generate book pages
// 3. Update book navigation
// 4. Link to games/curriculum

const result = {
  action: 'update_book_pages',
  pagesGenerated: ['books/episode1.html'],
  navigationUpdated: true,
  status: 'success'
};

return {
  json: {
    ...update,
    result
  }
};

// Garvis: Aggregate website update results
const inputItems = $input.all();
const results = {};

inputItems.forEach(item => {
  const data = item.json;
  if (data.result) {
    results[data.result.action] = data.result;
  }
});

const allSuccess = Object.values(results).every(r => r.status === 'success');

return {
  json: {
    jobId: inputItems[0]?.json?.jobId || 'unknown',
    status: allSuccess ? 'completed' : 'partial',
    results,
    deployed: results.deploy_netlify?.status === 'success',
    timestamp: new Date().toISOString()
  }
};
